mapper.insert:
INSERT INTO TB_BILL
	( BILL_NO
	  , USE_DTL_CD
	  , USE_DATE
	  , USE_PRICE
	  , FILE_NM
	  , FILE_PATH 
	  , REG_DATE)
VALUES ( 
		(SELECT NVL(MAX(BILL_NO), 0) + 1 FROM TB_BILL)
	  , #{useDtlCd, jdbcType=VARCHAR}
	  , #{useDate, jdbcType=DATE}
	  , #{usePrice, jdbcType=VARCHAR}
	  , #{fileNm, jdbcType=VARCHAR}
	  , #{filePath, jdbcType=VARCHAR}
	  , SYSDATE )

mapper.select:
SELECT BILL_NO , NO , D.USE_DTL_NM , TO_CHAR(USE_PRICE,'999,999,999,999') AS USE_PRICE , TO_CHAR(USE_DATE,'yyyy-mm-dd') AS USE_DATE , TO_CHAR(APPROVE_PRICE,'999,999,999,999') AS APPROVE_PRICE , S.STATE_NM , TO_CHAR(STATE_DATE,'yyyy-mm-dd') AS STATE_DATE , ETC , FILE_NM , FILE_PATH , TO_CHAR(REG_DATE,'yyyy-mm-dd') AS REG_DATE , TO_CHAR(UP_DATE,'yyyy-mm-dd') AS UP_DATE FROM TB_BILL B LEFT OUTER JOIN TB_USEDETAIL D ON D.USE_DTL_CD = B.USE_DTL_CD LEFT OUTER JOIN TB_STATE S ON S.STATE_CD = B.STATE_CD WHERE 1=1
AND D.USE_DTL_CD = #{searchDetailCode, jdbcType=VARCHAR}
AND S.STATE_CD = #{searchStateCode, jdbcType=VARCHAR}
ORDER BY REG_DATE DESC
